var client = new Web3Client(apiUrl, new Client());
final EthereumAddress contractAddr =EthereumAddress.fromHex('0xED771E490468731b608c63684Bb81D4d8609f7Ac');


final contract =DeployedContract(ContractAbi.fromJson(abiCode, "Tether") , contractAddr);


    // extracting some functions and events that we'll need later
  final transferEvent = contract.event('Transfer');
  final balanceFunction = contract.function('balances');
  final sendFunction = contract.function('sendCoin');
  final totalSupply = contract.function('totalSupply');
  // listen for the Transfer event when it's emitted by the contract above
  final subscription = client
      .events(FilterOptions.events(contract: contract, event: transferEvent))
      .take(1)
      .listen((event) {
    final decoded = transferEvent.decodeResults(event.topics, event.data);

    final from = decoded[0] as EthereumAddress;
    final to = decoded[1] as EthereumAddress;
    final value = decoded[2] as BigInt;

    print('$from sent $value MetaCoins to $to');
  });


