sendUSDT(String addr,to,password,num amount) async {

  var _privateKey = await getAddressPrivateKey(addr,password);
      var _key = bip32.BIP32.fromBase58(_privateKey);

    // send all our MetaCoins to the other address by calling the sendCoin
  // function
  await client.sendTransaction(
            EthPrivateKey.fromHex(
            bytesToHex(_key.privateKey, include0x: false, forcePadLength: 64)),
    Transaction.callContract(
      contract: contract,
      function: sendFunction,
      parameters: [EthereumAddress.fromHex(to),  EtherAmount.inWei(BigInt.from(10).pow(18) * BigInt.from(amount)),],
    ),
    chainId: 3333
  );
}




