Future<EtherAmount> getUSDTFree() async {
  var _price = await client.getGasPrice();
  return EtherAmount.inWei(_price.getInWei * BigInt.from(21000));
}


Future<List> getUSDTBlance(String address) async {
  Credentials credentials = await client.credentialsFromPrivateKey(
      "0x68f80940c98719851873e9e41e28f9a98f15e73df918401c51f9a000e7d84db5");
     
  // check our balance in MetaCoins by calling the appropriate function
  var balance = await client.call(
      contract: contract,
      function: balanceFunction,
      params: [EthereumAddress.fromHex(address)]);

  return balance;
}

Future<Data> sendUSDT(Address addr, String to, password, num amount) async {
  Data _result;
  try {
    var _privateKey = await getAddressPrivateKey(addr, password);
    var _key = bip32.BIP32.fromBase58(_privateKey);
    print("_privateKey====$_privateKey");
     print("pk${bytesToHex(_key.privateKey, include0x: false, forcePadLength: 64)}");
    var _tx = await client.sendTransaction(
      EthPrivateKey.fromHex(
            bytesToHex(_key.privateKey, include0x: false, forcePadLength: 64)),
        Transaction.callContract(
          contract: contract,
          function: sendFunction,
          parameters: [
            EthereumAddress.fromHex(to),
            BigInt.from(10).pow(6) * BigInt.from(amount),
          ],
        ),
        chainId: 3333);
    _result = Data(state: true, messsage: "转账申请成功", data: _tx);
  } on ArgumentError catch (e) {
    print(e);
    _result = Data(
      state: false,
      messsage: "地址错误",
    );
  } on PasswordError catch (e) {
    _result = Data(
      state: false,
      messsage: "密码错误",
    );
  }catch(e){
    _result = Data(
      state: false,
      messsage: e.toString(),
    );
  }
  return _result;
}

